import React from "react";
import logo from "./logo.svg";
import "./App.css";

/*
  날짜 : 2020/10/19
  이름 : 김보미
  내용 : JSX 실습하기

    JSX
      - JAVASCRIPT XML
      - 리액트 컴포넌트에서 브라우저에 렌더링 하기 위해 사용하는 XML 문법


*/
function App() {
  // 상수
  const title = "안녕 리액트!";
  const message = "리액트 JSX 문법 실습하기";
  const name = "홍길동";

  // 변수
  let num1 = 1;
  let num2  = 2;

  let var1 = true;
  let var2 = false;

  let str1 = "Hello";
  let str2 = "안녕하세요.";

  return (
    <div>
      <h3>ch02. JSX 실습하기</h3>

      <h4>자바스트립트(E56) 변수 출력</h4>
      <p>title : {title}</p>
      <p>message : {message}</p>

      <h4>조건부 연산자</h4>
      <p>
        {num1 > num2 ? (
        <span>num1은 num2보다 크다.</span>
        ) : (
        <span>num1은 num2보다 작다.</span>
        )}
      </p>

    </div>
  );
}

export default App;
